import styled from 'styled-components'

export const Titulo = styled.h2`
    width: 100%;
    padding: 30px 0;
    background-color: #fff;
    color: #eb9b00;
    font-size: 36px;
    text-align: center;
    margin: 0;
    text-shadow: 2px 2px 2px rgba(0,0,0,.5);
   
`
